import React, { useState, useEffect } from "react";
import axios from "axios";

function AvailableDonors() {
  const [donors, setDonors] = useState([]);
  const [filteredDonors, setFilteredDonors] = useState([]);
  const [searchText, setSearchText] = useState("");
  const [bloodTypeFilter, setBloodTypeFilter] = useState("");

  useEffect(() => {
    async function fetchDonors() {
      const response = await axios.get("/api/donors");
      setDonors(response.data);
      setFilteredDonors(response.data);
    }

    fetchDonors();
  }, []);

  useEffect(() => {
    const filtered = donors.filter(
      (donor) =>
        donor.name.toLowerCase().includes(searchText.toLowerCase()) &&
        (bloodTypeFilter === "" || donor.bloodType === bloodTypeFilter)
    );
    setFilteredDonors(filtered);
  }, [searchText, bloodTypeFilter, donors]);

  function handleSearchTextChange(event) {
    setSearchText(event.target.value);
  }

  function handleBloodTypeFilterChange(event) {
    setBloodTypeFilter(event.target.value);
  }

  return (
    <div>
      <h1>Available Donors for Blood Donation</h1>
      <div>
        <label htmlFor="searchText">Search:</label>
        <input
          type="text"
          id="searchText"
          value={searchText}
          onChange={handleSearchTextChange}
        />
        <label htmlFor="bloodTypeFilter">Filter by blood type:</label>
        <select
          id="bloodTypeFilter"
          value={bloodTypeFilter}
          onChange={handleBloodTypeFilterChange}
        >
          <option value="">All</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Blood Type</th>
            <th>Location</th>
            <th>Contact Info</th>
          </tr>
        </thead>
        <tbody>
          {filteredDonors.map((donor) => (
            <tr key={donor.id}>
              <td>{donor.name}</td>
              <td>{donor.bloodType}</td>
              <td>{donor.location}</td>
              <td>{donor.contactInfo}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Login;
