import React, { useState, useEffect } from "react";
import mongoose from "mongoose";

// Connect to MongoDB using Mongoose
mongoose.connect("mongodb://localhost:27017/myapp", {
  useNewUrlParser: true,
  useUnifiedTopology: true,
});

// Define the schema for the blood donor profile data
const donorSchema = new mongoose.Schema({
  name: String,
  email: String,
  phone: String,
  bloodGroup: String,
  lastDonatedDate: Date,
  totalDonations: Number,
  availableStatus: String,
});

// Create the blood donor profile model
const Donor = mongoose.model("Donor", donorSchema);

function Login() {
  const [donorData, setDonorData] = useState(null);
  const [availableStatus, setAvailableStatus] = useState("Available");

  // Fetch the donor profile data from the database when the component mounts
  useEffect(() => {
    async function fetchData() {
      try {
        const donor = await Donor.findOne({ email: "[Email]" });
        setDonorData(donor);
      } catch (err) {
        console.error(err);
      }
    }
    fetchData();
  }, []);

  // Update the donor's available status in the database
  async function handleAvailableStatusChange(newStatus) {
    try {
      const updatedDonor = await Donor.findOneAndUpdate(
        { email: "[Email]" },
        { availableStatus: newStatus },
        { new: true }
      );
      setDonorData(updatedDonor);
      setAvailableStatus(updatedDonor.availableStatus);
    } catch (err) {
      console.error(err);
    }
  }

  // Render the blood donor profile page with the donor's data
  return (
    <div className="donor-profile">
      {donorData ? (
        <div className="donor-details">
          <h1 className="donor-name">{donorData.name}</h1>
          <p className="donor-email">{donorData.email}</p>
          <p className="donor-phone">{donorData.phone}</p>
          <p className="donor-blood-group">{donorData.bloodGroup}</p>
          <p className="donor-last-donated-date">
            Last Donated: {donorData.lastDonatedDate}
          </p>
          <p className="donor-total-donations">
            Total Donations: {donorData.totalDonations}
          </p>
          <div className="available-status">
            <span>Status:</span>
            <select
              value={availableStatus}
              onChange={(e) => handleAvailableStatusChange(e.target.value)}
            >
              <option value="Available">Available</option>
              <option value="Busy">Busy</option>
              <option value="Away">Away</option>
            </select>
          </div>
        </div>
      ) : (
        <p>Loading donor data...</p>
      )}
    </div>
  );
}

export default Login;
