import React, { useState, useEffect } from "react";
import axios from "axios";

function Login() {
  const express = require('express');
const { Pool } = require('pg');

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: {
    rejectUnauthorized: false
  }
});

const app = express();

app.get('/api/requests', async (req, res) => {
  try {
    const { rows } = await pool.query('SELECT * FROM requests');
    res.json(rows);
  } catch (err) {
    console.error(err);
    res.status(500).send('Internal server error');
  }
});

app.listen(process.env.PORT || 3000, () => {
  console.log(`Server running on port ${process.env.PORT || 3000}`);
});

  const [requests, setRequests] = useState([]);
  const [filteredRequests, setFilteredRequests] = useState([]);
  const [statusFilter, setStatusFilter] = useState("all");

  useEffect(() => {
    async function fetchData() {
      const result = await axios.get("/api/requests");
      setRequests(result.data);
      setFilteredRequests(result.data);
    }
    fetchData();
  }, []);

  const handleFilterChange = (event) => {
    const status = event.target.value;
    setStatusFilter(status);
    if (status === "all") {
      setFilteredRequests(requests);
    } else {
      setFilteredRequests(requests.filter((request) => request.status === status));
    }
  };

  return (
    <div>
      <h1>Requests</h1>
      <label htmlFor="statusFilter">Filter by status:</label>
      <select id="statusFilter" value={statusFilter} onChange={handleFilterChange}>
        <option value="all">All</option>
        <option value="pending">Pending</option>
        <option value="approved">Approved</option>
        <option value="denied">Denied</option>
      </select>
      <ul>
        {filteredRequests.map((request) => (
          <li key={request.id}>
            {request.title} - {request.status}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Login;
